mapper.sql:
FROM userUi as a WHERE 1=1
AND a.seq = #{shValue}
ORDER BY a.seq DESC

mapper.select:
SELECT aa.* FROM ( SELECT a.seq, a.firstName,
a.lastName, a.gender, a.btd, a.email, a.passWord, a.price, a.score, a.delNy, a.admSignin, a.mobileCarrier
) aa limit #{rowNumToShow} offset #{startRnumForMysql}
