mapper.sql:
FROM userUi as a WHERE 1=1
AND a.seq = #{shValue}
ORDER BY a.seq DESC

mapper.select:
SELECT aa.* FROM ( SELECT a.seq, a.firstName,
a.lastName, a.gender, a.btd, a.email, a.passWord, a.price, a.score, a.delNy, a.admSignin
) aa limit #{pageNumToShow} offset #{startRnumForMysql}

mapper.update:
UPDATE codeGroup SET
	ifcgName = #{ifcgName},
	ifcgDelNy = #{ifcgDelNy},
	ifcgUseNy = #{ifcgUseNy}
WHERE ifcgSeq = #{ifcgSeq}
